<?phprequire_once 'Project/Code/ApplicationsFramework/MVC_superClasses/applicationsSuperView.php';require_once 'Project/Code/1_Website/Applications/Seeker/Blocks/shortlistBlockController.php';class hcpView extends applicationsSuperView{		private $health_care_provider_info_array;	private $hcp_images;	private $city;	private $state;	private $zipcode;	private $amenities_categories;	private $amenities;	public $uri;//-------------------------------------------------------------------------------------------------			public function _get($field)	{		if(property_exists($this, $field)) return $this->{$field};				else return NULL;	}	//-------------------------------------------------------------------------------------------------		public function _set($field, $value) { if(property_exists($this, $field)) $this->{$field} = $value; }//-------------------------------------------------------------------------------------------------	public function showHealthCareProvider()	{		$this->displayJSandCSS();				$shortlist = new shortlistBlockController();				$content = $this->renderTemplate('Project/Design/1_Website/Applications/Health_Care_Provider/templates/health_care_provider_template.phtml');		response::getInstance()->addContentToTree(array('MAIN_CONTENT'=>$content));			}		private function displayJSandCSS()	{		$templates_folder = 'Project/Design/1_Website/Applications/Health_Care_Provider/templates/';			$content = $this->renderTemplate($templates_folder.'css_links.phtml');		response::getInstance()->addContentToStack('css_and_javascript_links_for_this_page_only',array('CURRENT SECTION CSS'=>$content));			$content = $this->renderTemplate($templates_folder.'inpage_javascript.js');		response::getInstance()->addContentToStack('inpage_javascript_top',array('HCP_JS'=>$content));				$content = $this->renderTemplate('Project/Design/1_Website/Applications/Seeker/templates/blocks/shortlist_javascript.js');		response::getInstance()->addContentToStack('inpage_javascript_top',array('SHORTLIST_JS'=>$content));			$content = $this->renderTemplate('Project/Design/1_Website/Applications/_Common/templates/js_and_css_links.phtml');		response::getInstance()->addContentToStack('css_and_javascript_links_for_this_page_only',array('COMMON CSS AND JS'=>$content));			}		public function displayThankYouPopUp()	{		 $content =  $this->renderTemplate('Project/Design/1_Website/Applications/Health_Care_Provider/templates/thank_you_template.php');		 response::getInstance()->addContentToTree(array('THANK_YOU'=>$content));	}	}?>