<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/controllerSuperClass_Core/controllerSuperClass_Core.php';require_once('Model.php');require_once('View.php');require_once 'Project/Code/1_Website/Pages/primary/seeker/Modules/seekerSession.php';
class controller extends controllerSuperClass_Core{	public function indexAction()	{		require_once 'Project/Code/System/ZipCodes/zipcodes.php';				$zipcodes = new zipcodes();				$view = new view();		$view->_set('states', $zipcodes->selectStates());				$view->renderAll();	}		public function stateAction()	{		require_once 'Project/Code/System/ZipCodes/zipcodes.php';				$state = $this->getValueOfURLParameterPair('state');				$zipcodes	= new zipcodes();		$cities		= $zipcodes->selectCityOrStateSearch('city', 'state', $state);				$view = new view();		$view->_set('cities', $cities);		$view->renderAll('cities_template');	}		public function cityAction()	{		$city = $this->getValueOfURLParameterPair('city');				$this->getArrayOfHomes('city', $city);	}		public function zipcodeAction()	{		$zipcode = $this->getValueOfURLParameterPair('zipcode');				$this->getArrayOfHomes('zipcode', $zipcode);	}		public function houseAction()	{		if(seekerSession::ifSeekerSessionExists() == TRUE)		{			require_once 'Project/Code/System/HealthCare_Provider/healthcare_provider.php';						$provider_id = $this->getValueOfURLParameterPair('house');						$healthcare_provider = new healthcare_provider();			$healthcare_provider->__set('provider_id', $provider_id);			$healthcare_provider->select();						$view = new view();			$view->_set('provider', $healthcare_provider);			$view->renderAll('provider_template');		}				else			header('Location: /seeker');	}		private function getArrayOfHomes($column, $value)	{		require_once 'Project/Code/System/HealthCare_Provider/healthcare_providers.php';				$healthcare_providers	= new healthcare_providers();				if ($column == 'zipcode')			$array_of_homes	= $healthcare_providers->selectByZipCode($column, $value);		else 			$array_of_homes			= $healthcare_providers->selectByCityOrZipCode($column, $value);				$view = new view();		$view->_set('array_of_homes', $array_of_homes);		$view->renderAll('homes_template');	}		}?>