<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/controllerSuperClass_Core/controllerSuperClass_Core.php';require_once('Model.php');require_once('View.php');class controller extends controllerSuperClass_Core {	public function submitEmailAjaxAction()	{		require_once 'Project/Code/1_Website/Applications/User_Account/Modules/mail/email.php';				$from_email = $_REQUEST['email'];		$from_name 	= $_REQUEST['name'];		$subject 	= 'New Inquiry';		$body 		= "			Name: {$_REQUEST['name']}<br>			Email: {$_REQUEST['email']}<br>			Mobile: {$_REQUEST['mobile']}<br>			Message: {$_REQUEST['inquiry']}<br>		";				email::send_email(NULL, NULL, $from_email, $from_name, $subject, $body);		jQuery::getResponse();	}}?>