<?phprequire_once(FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php');require_once 'Project/Code/1_Website/Applications/User_Account/Modules/userSession.php';class view extends viewSuperClass_Core{		private $list_of_states;	//-------------------------------------------------------------------------------------------------		public function _get($field)	{		if(property_exists($this, $field)) return $this->{$field};				else return NULL;	}	//-------------------------------------------------------------------------------------------------		public function _set($field, $value) { if(property_exists($this, $field)) $this->{$field} = $value; }//-------------------------------------------------------------------------------------------------	public function showHome()	{				$content = $this->renderTemplate('Project/Design/1_Website/Pages/primary/home/templates/home_template.phtml');		response::getInstance()->addContentToTree(array('HOME'=>$content));				$content = $this->renderTemplate('Project/Design/1_Website/Pages/primary/home/templates/get_started_template.phtml');		response::getInstance()->addContentToTree(array('GET_STARTED'=>$content));				$content = $this->renderTemplate('Project/Design/1_Website/Pages/primary/home/templates/states_template.phtml');		response::getInstance()->addContentToTree(array('STATES'=>$content));			}	
}?>