<?phprequire_once 'Zend/Session.php';require_once 'seekerObject.php';class seekerSession{	 	public static function saveSeekerSession($seeker) 	{		Zend_Session::rememberMe(172800);		$seekerSession = new Zend_Session_Namespace('seeker');		$seekerSession->__set('seeker', $seeker);		$seekerSession->seekerInfoExists = TRUE;	}	 //=================================================================================================	 public static function getSeekerSession()	 { 		$seekerSession = new Zend_Session_Namespace('seeker'); 		 		if(isset($seekerSession->seekerInfoExists)) 		{ 			$seeker = $seekerSession->__get('seeker'); 			$seeker = unserialize(serialize($seeker)); 			 			return $seeker; 		} 		 	else	 		return new seekerObject();	 }	 //=================================================================================================	 public static function ifSeekerSessionExists()	 { 		$seekerSession = new Zend_Session_Namespace('seeker'); 		 		if(isset($seekerSession->seekerInfoExists)) 			return TRUE; 		 	else	 		return FALSE;	 }	 //=================================================================================================	public static function unsetSeekerSession()	{		$seekerSession = new Zend_Session_Namespace('seeker');		$seekerSession->unsetAll();	}	 }?>