<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/controllerSuperClass_Core/controllerSuperClass_Core.php';require_once('Model.php');require_once('View.php');require_once 'Project/Code/1_Website/Pages/primary/seeker/Modules/seekerSession.php';require_once 'Project/Code/System/HealthCare_Provider/healthcare_provider.php';require_once 'Project/Code/System/HealthCare_Provider/healthcare_providers.php';
class controller extends controllerSuperClass_Core {	public function indexAction()	{		$zipcodesArray = array();				if(isset($_POST['search']))		{			require_once 'Project/Code/System/ZipCodes/zipcodes.php';					$zipcodes		= new zipcodes();			$zipcodesArray	= $zipcodes->selectZipCodeSearch($_POST['zipcode']);		}				$view = new view();		$view->_set('zipcodes', $zipcodesArray);		$view->renderAll();	}
}?>